import React, { useState, useEffect } from "react";
import "./videos_assets/css/main.css";
import "./videos_assets/css/fontawesome-all.min.css";
import "./videos_assets/css/noscript.css";
import "./videos_assets/css/videos-custom.css"; // reuse custom fade/layout styling

function Contact() {
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("");
  const [verse, setVerse] = useState(null);
  const [loadingVerse, setLoadingVerse] = useState(true);

  // Fetch a daily devotional verse from a public API
  useEffect(() => {
    const fetchVerse = async () => {
      try {
        const res = await fetch("https://beta.ourmanna.com/api/v1/get/?format=json");
        const data = await res.json();
        if (data && data.verse && data.verse.details) {
          setVerse({
            text: data.verse.details.text,
            reference: data.verse.details.reference,
          });
        }
      } catch (error) {
        console.error("Error fetching daily verse:", error);
        setVerse({
          text: "‚ÄúThe grass withers, the flower fades, but the word of our God will stand forever.‚Äù",
          reference: "Isaiah 40:8",
        });
      } finally {
        setLoadingVerse(false);
      }
    };
    fetchVerse();
  }, []);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("/api/sendEmail", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        setStatus("‚úÖ Message sent successfully! Thank you for reaching out.");
        setFormData({ name: "", email: "", message: "" });
      } else {
        setStatus("‚ùå Failed to send message. Please try again later.");
      }
    } catch (err) {
      setStatus("‚ö†Ô∏è Error sending message.");
    }
  };

  return (
    <div id="wrapper">
      <div id="bg"></div>
      <div id="overlay"></div>

      <div id="main">
        {/* Header */}
        <header id="header" className="videos-header">
          <h1>Contact Jeremiah3122 Ministry</h1>
          <p>‚ÄúLet everything you do be done in love.‚Äù ‚Äî 1 Corinthians 16:14</p>
        </header>

        {/* Daily Devotional Section */}
        <section className="devotional-section fade-in">
          <h2>üìñ Daily Devotional</h2>
          {loadingVerse ? (
            <p>Loading today‚Äôs verse...</p>
          ) : verse ? (
            <blockquote className="devotional-verse">
              ‚Äú{verse.text}‚Äù
              <footer>‚Äî {verse.reference}</footer>
            </blockquote>
          ) : (
            <p>Unable to load verse today. Please check back later.</p>
          )}
        </section>

        {/* Contact Form */}
        <section className="contact-section fade-in">
          <h2>Send Us a Message</h2>
          <form className="contact-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="email"
                name="email"
                placeholder="Your Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <textarea
                name="message"
                placeholder="Your Message"
                value={formData.message}
                onChange={handleChange}
                required
              ></textarea>
            </div>
            <button type="submit" className="send-btn">
              Send Message
            </button>
          </form>
          {status && <p className="status-message">{status}</p>}
        </section>

        {/* Footer */}
        <footer id="footer">
          <section>
            <p>
              &copy; {new Date().getFullYear()} Jeremiah3122 Ministry. Design
              inspired by <a href="http://html5up.net">HTML5 UP</a>.
            </p>
          </section>
        </footer>
      </div>
    </div>
  );
}

export default Contact;
